mapper.update:
update tb_comment
set comment_date = sysdate, reply_content = #{rContent}
where reply_no = #{rNo}

update tb_comment
set comments_delete = 'Y'
where reply_no = #{rNo}

update tb_comment_re
set COMMENTS_DELETE = 'Y'
where re_reply_no = #{rrNo}

update tb_comment_re
set reply_content = ${rContent}, COMMENT_DATE = default
where re_reply_no = #{rrNo}
