mapper.update:
		UPDATE TB_USER SET ACCEPT='Y'
		WHERE USER_ID IN
		
            #{item}

		UPDATE TB_USER SET ACCEPT = 'Y' 
		WHERE USER_ID IN 
		
            #{item}

mapper.delete:
		DELETE FROM TB_USER 
		WHERE USER_ID IN
		
            #{item}

mapper.select:
SELECT COUNT(*) FROM TB_BOARD
JOIN TB_USER USING(USER_ID)
WHERE APT_NAME = #{aptName}
