mapper.update:
update tb_notice
set notice_count = notice_count +1
where notice_no = #{nNo}

    	update tb_file
    	set file_name = #{renameFileName}
		where notice_no = #{nNo}

update tb_notice
set notice_title = #{nTitle}, notice_content = #{nContent}
where notice_no = #{nNo}

update tb_notice
set NOTICE_DELETE = 'Y'
where NOTICE_NO = #{nNo}

update tb_comment
set REPLY_CONTENT = #{rContent}, COMMENT_DATE = SYSDATE
where REPLY_NO = #{rNo}

update tb_comment
set COMMENTS_DELETE = 'Y'
where REPLY_NO = #{rNo}
