mapper.update:
update tb_board
set BOARD_DELETE = 'Y'
where BOARD_NO = #{boardNo}

update tb_club
set club_name = #{clubName}, club_intro = #{clubIntro}, club_plan = #{clubPlan}, club_place = #{clubPlace}, club_etc = #{clubEtc}, max_people = #{maxPeople}
where board_no = #{boardNo}

mapper.select:
select count(*)
from tb_club_join
where club_name = #{clubName} and user_id = #{userId}
