mapper.update:
update tb_board
set board_count = board_count + 1
where board_no = #{boardNo}

update tb_board
set board_delete='Y'
where board_no = #{boardNo}

update tb_board  
set board_title=#{boardTitle}, board_content=#{boardContent}
where board_no=#{boardNo}

update tb_market
set market_price=#{price}
where board_no=#{boardNo}
