mapper.update:
update tb_user set user_name = #{userName}, nickname = #{nickName}, phone = #{phone}, email = #{email}, apt_name = #{aptName}, apt_dong = #{aptDong}, apt_hosu = #{aptHosu}
,user_pwd = #{userPwd}
where user_id = #{userId} AND user_delete = 'N'

update tb_user
set user_file = #{userFile}
where user_id = #{userId} AND user_delete = 'N'

mapper.select:
select count(*)
from tb_board
where user_id = #{userId}

select count(*)
from tb_comment
where user_id = #{userId}

select QNA_ID, QNA_TITLE, QNA_CONTENT, USER_ID, QNA_DATE, FILE_NAME, DELETE_YN
from tb_QnA
left join tb_file
using(QNA_ID)
where user_id = #{userId}
